mapper.update:
UPDATE diary SET diaryTitle=#{diaryTitle}, diaryContent=#{diaryContent}, 
	saveImgName=#{saveImgName, jdbcType=VARCHAR} 
WHERE diaryNum=#{diaryNum}

mapper.select:
SELECT diaryImgNum, diaryNum, saveImgName
FROM diaryImg
WHERE diaryNum=#{diaryNum}

	SELECT diaryImgNum, diaryNum, saveImgName
	FROM diaryImg
    WHERE diaryImgNum=#{diaryImgNum}
