mapper.update:
UPDATE product_review SET(answer, a_date)
VALUES(#{answer}, #{a_date})
WHERE num = #{num}

mapper.select:
SELECT p.code, product_name, TO_CHAR(start_date,'YYYY-MM-DD') start_date, TO_CHAR(end_date, 'YYYY-MM-DD') end_date, upload_date, ishidden, price, img_name from product p LEFT JOIN productdetail d ON p.code=d.code JOIN product_img i ON p.code=i.code WHERE category_num IN
#{code}
ORDER BY START_DATE ASC OFFSET #{offset} ROWS FETCH FIRST #{rows} ROWS ONLY
